import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const UserDashboard = ({ userData }) => {
  const navigate = useNavigate();
  const [recommendations, setRecommendations] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [errorDetails, setErrorDetails] = useState(null);

  const colors = {
    primary: '#8B80F9',
    secondary: '#A594F9',
    light: '#EAE6FF',
    text: '#2A2D43',
    danger: '#FF6B6B'
  };

  useEffect(() => {
    if (!userData) {
      navigate('/questionnaire');
      return;
    }

    const fetchRecommendations = async () => {
      try {
        setLoading(true);
        setError(null);
        setErrorDetails(null);

        const response = await fetch('http://localhost:3001/api/generateMealPlan', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(userData),
        });

        const data = await response.json();
        if (!response.ok || !data.success) {
          throw new Error(data.message || 'Failed to generate meal plan');
        }

        setRecommendations(data.mealPlan);
        setLoading(false);
      } catch (err) {
        setError(`Failed to load recommendations: ${err.message}`);
        setErrorDetails(err.stack || "No additional details available");
        setLoading(false);
      }
    };

    fetchRecommendations();
  }, [userData, navigate]);

  if (loading) {
    return (
      <div className="container text-center my-5">
        <h2 style={{ color: colors.text }}>Loading your nutrition plan...</h2>
        <div className="spinner-border" style={{ color: colors.primary }}></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container my-5">
        <div className="alert alert-danger">{error}</div>
        {errorDetails && <pre>{errorDetails}</pre>}
      </div>
    );
  }

  if (!recommendations) {
    return (
      <div className="container text-center my-5">
        <h2 style={{ color: colors.text }}>No recommendations available</h2>
      </div>
    );
  }

  return (
    <div className="container py-4">
      <h1 style={{ color: colors.text }}>Your Nutrition Plan</h1>
      <h2 style={{ color: colors.text, marginBottom: '1.5rem' }}>Today's Meal Plan</h2>

      {/* Displaying meal plans */}
      {['breakfast', 'lunch', 'dinner', 'snacks'].map((mealType) => (
        <div key={mealType} className="card mb-3 p-3" style={{ background: colors.light }}>
          <h3 style={{ color: colors.primary }}>{mealType.charAt(0).toUpperCase() + mealType.slice(1)}</h3>
          <h4>{recommendations.dailyPlan[mealType].name}</h4>
          <p><strong>Ingredients:</strong> {recommendations.dailyPlan[mealType].ingredients.join(', ')}</p>
          <p><strong>Preparation:</strong> {recommendations.dailyPlan[mealType].preparation}</p>
        </div>
      ))}
    </div>
  );
};

export default UserDashboard;
