import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const UserDashboard = ({ userData }) => {
  const navigate = useNavigate();
  const [recommendations, setRecommendations] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeDay, setActiveDay] = useState(new Date().getDate());

  const colors = {
    primary: '#8B80F9',
    secondary: '#A594F9',
    light: '#EAE6FF',
    accent1: '#FFD6E0',
    accent2: '#A1EAFB',
    text: '#2A2D43',
    success: '#77DD77',
    warning: '#FFB347',
    danger: '#FF6B6B'
  };

  useEffect(() => {
    if (!userData) {
      navigate('/questionnaire');
      return;
    }

    const fetchRecommendations = async () => {
      try {
        setLoading(true);
        setError(null);

        const response = await fetch('http://localhost:3001/api/generateMealPlan', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(userData),
        });

        const data = await response.json();
        if (!response.ok || !data.success) throw new Error(data.message || 'Failed to generate meal plan');

        setRecommendations(data.mealPlan);
        setLoading(false);
      } catch (err) {
        setError(`Failed to load your recommendations: ${err.message}`);
        setLoading(false);
      }
    };

    fetchRecommendations();
  }, [userData, navigate]);

  if (loading) {
    return <div className="text-center my-5"><h2>Loading...</h2></div>;
  }

  if (error) {
    return <div className="alert alert-danger text-center">{error}</div>;
  }

  if (!recommendations) {
    return <div className="text-center my-5"><h2>No recommendations available</h2></div>;
  }

  return (
    <div className="container py-4">
      {/* Nutrition Plan Header */}
      <div className="card border-0 rounded-4 shadow-sm mb-4" style={{ background: 'white' }}>
        <div className="card-body p-4 d-flex justify-content-between align-items-center">
          <h1 style={{ color: colors.text, fontSize: '1.8rem' }}>Your Nutrition Plan</h1>
          <div>
            <button className="btn btn-outline-primary me-2" onClick={() => navigate('/questionnaire')}>Update Profile</button>
            <button className="btn btn-primary" onClick={() => window.location.reload()}>Refresh Plan</button>
          </div>
        </div>
      </div>

      {/* User Basic Information Section */}
      <div className="card border-0 rounded-4 shadow-sm mb-4" style={{ background: colors.light }}>
        <div className="card-body p-4">
          <h2 style={{ color: colors.text, fontSize: '1.5rem', marginBottom: '1rem' }}>Your Basic Information</h2>
          <div className="row">
            <div className="col-md-4">
              <p><strong>Full Name:</strong> {userData.basicInformation.fullName}</p>
            </div>
            <div className="col-md-2">
              <p><strong>Age:</strong> {userData.basicInformation.age}</p>
            </div>
            <div className="col-md-2">
              <p><strong>Weight:</strong> {userData.basicInformation.weight.value} {userData.basicInformation.weight.unit}</p>
            </div>
            <div className="col-md-2">
              <p><strong>Height:</strong> {userData.basicInformation.height.value} {userData.basicInformation.height.unit}</p>
            </div>
            <div className="col-md-2">
              <p><strong>Blood Type:</strong> {userData.basicInformation.bloodType}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Meal Plan Recommendations */}
      <div className="row">
        <div className="col-lg-8">
          <div className="card border-0 rounded-4 shadow-sm mb-4">
            <div className="card-body p-4">
              <h2 style={{ color: colors.text, fontSize: '1.5rem' }}>Today's Meal Plan</h2>
              <h4 style={{ color: colors.primary, fontSize: '1.2rem', marginBottom: '1rem' }}>
                {recommendations.dailyPlan.breakfast.name}
              </h4>
              <ul>
                {recommendations.dailyPlan.breakfast.ingredients.map((ingredient, idx) => (
                  <li key={idx}>{ingredient}</li>
                ))}
              </ul>
            </div>
          </div>
        </div>

        <div className="col-lg-4">
          {/* Nutritional Focus */}
          <div className="card border-0 rounded-4 shadow-sm mb-4">
            <div className="card-body p-4">
              <h3 style={{ color: colors.text, fontSize: '1.3rem', marginBottom: '1rem' }}>Nutritional Focus</h3>
              {recommendations.nutritionalFocus.map((item, idx) => (
                <div key={idx} className="card mb-3 border-0 rounded-3" style={{ background: colors.light }}>
                  <div className="card-body p-3">
                    <h4 style={{ color: colors.primary, fontSize: '1.1rem', marginBottom: '0.5rem' }}>{item.nutrient}</h4>
                    <p>{item.reason}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Foods to Limit */}
          <div className="card border-0 rounded-4 shadow-sm mb-4">
            <div className="card-body p-4">
              <h3 style={{ color: colors.text, fontSize: '1.3rem', marginBottom: '1rem' }}>Foods to Limit</h3>
              {recommendations.avoidList.map((item, idx) => (
                <div key={idx} className="card mb-3 border-0 rounded-3" style={{ background: colors.light }}>
                  <div className="card-body p-3">
                    <h5 style={{ color: colors.danger, fontSize: '1rem', margin: 0 }}>{item.food}</h5>
                    <p>{item.reason}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Weekly Habit Tips */}
          <div className="card border-0 rounded-4 shadow-sm">
            <div className="card-body p-4">
              <h3 style={{ color: colors.text, fontSize: '1.3rem', marginBottom: '1rem' }}>Weekly Habit Tips</h3>
              <ul>
                {recommendations.weeklyHabitTips.map((tip, idx) => (
                  <li key={idx}>{tip}</li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
